import React from 'react'
import alphabet_hash from './alphabet_hash'
import QuestionLetter from './QuestionLetter'
import ScoreTracker from './ScoreTracker'
import ChoiceContainer from './ChoiceContainer'


class GameContainer extends React.Component {
  constructor(){
    super()
      this.state = {
        alphabetArray: alphabet_hash,
        lettersRemaining: alphabet_hash,
        currentLetter: {},
        choices: [],
        selectedChoice: null,
        numOfAsked: 0,
        numCorrect: 0,
      }
  }

  componentDidMount(){
    let choices = []
      let correctLetter = this.state.lettersRemaining[Math.round(Math.random()*this.state.lettersRemaining.length)]
      choices.push(correctLetter)

      // this.state.lettersRemaining.filter(letter){
      //   return letter.character != correctLetter.character
      // }
      //filter lettersremaining, if the iterable letter does not equal
    this.setState({
      currentLetter: correctLetter,
      choices: choices,
      // lettersRemaining: filteredLetters
    })
  }

  //need an array to push the correct letter into
  //need to push 5 random letters into this array
  //array must be exactly 6 elements in length before rendering to DOM
  //answer must be pushed in last to splice in at a random integer (length of array)

  //should I give each object in alphabet_hash an id?

  updateSelectedChoice = (event) => {
    event.preventDefault()
    this.setState({selectedChoice: event.target.value})
    console.log("attempting to update choice...", event.target.value)
  }

  handleSubmit = (event) => {
    event.preventDefault()
    console.log("attempting to handle submit...", event.target.value)
    //this function should also update state of:
    //lettersRemaining (filter out previous currentLetter),
    //currentLetter to another random letter in choices lettersRemaining,
    //choices (new array of choices),
    //selectedChoice (to null)
    //numOfAsked (numOfAsked ++)
    //numCorrect (if choice == currentletter) {numCorrect++}
  }

  render(){
    return(
      <React.Fragment>
        <ScoreTracker
          numCorrect={this.state.numCorrect}
          numOfAsked={this.state.numOfAsked}
        />
        <h3>Choose the correct sound for this letter:</h3>
        <QuestionLetter
          generateQuestionLetter={this.generateQuestionLetter}
          currentLetter={this.state.currentLetter}
        />
        <ChoiceContainer
          updateSelectedChoice={this.updateSelectedChoice}
          handleSubmit={this.handleSubmit}
        />
      </React.Fragment>
    )
  }
}

export default GameContainer
