import React from 'react'
import alphabet_hash from './alphabet_hash'
import QuestionLetter from './QuestionLetter'
import ScoreTracker from './ScoreTracker'
import ChoiceContainer from './ChoiceContainer'


class GameContainer extends React.Component {
  constructor(){
    super()
      this.state = {
        alphabetArray: alphabet_hash,
        lettersRemaining: alphabet_hash,
        currentLetter: {},
        choices: [],
        selectedChoice: null,
        numOfAsked: 0,
        numCorrect: 0,
      }
  }

  componentDidMount(){
    let letter = this.state.alphabetArray
    [
      Math.round(Math.random()*this.state.alphabetArray.length)
    ]
    this.setState({
      currentLetter: letter,
      choices: {...this.state.currentLetter}
    })
  }

  render(){
    return(
      <React.Fragment>
        <ScoreTracker
          numCorrect={this.state.numCorrect}
          numOfAsked={this.state.numOfAsked}
        />
        <h3>Choose the correct sound for this letter:</h3>
        <QuestionLetter
          generateQuestionLetter={this.generateQuestionLetter}
          currentLetter={this.state.currentLetter}
        />
        <ChoiceContainer/>
      </React.Fragment>
    )
  }
}

export default GameContainer
